// 打开window窗口
function openwindow(title,data,type){
    var ckwidth = '800px';
    var ckheight = '520px';

    if($(window).width() < '767'){
       ckwidth = $(window).width()+'px';
       ckheight= '100%';
    }
    layer.open({
       type: type,
       title: title,
       maxmin: true,
       shadeClose: false, //点击遮罩关闭层
       shade: 0,
       area : [ckwidth , ckheight],
       content: data,

    },  function(value, index, elem){
        alert(value); //得到value
        layer.close(index);
        window.location.reload();
    });
}
function del(url){
    layer.confirm('确定要删除吗?删除父级子栏目也将被删除,删除后不可回复,请谨慎操作.', {
        icon: 3,
        btn: ['确定','取消'], //按钮,
    }, function(index){
        $.ajax({
            type: "post",
            url: url,
            dataType: "json",
            success: function (e) {
                layer.close(index);
                if(e.error){
                    toastr.success(e.msg)
                }else{
                    toastr.error(e.msg)
                }
            }
        });

    });
}
